<div class="form-horizontal">    <div class="form-group between">      <label for="packageweight" class="col-md-3 label-customized">  	    <span class="required-field">*</span> Package Weight:  	  </label>      <div class="input-group input-box-size">        <input id="package_weight" name="package_weight" value="{{ $item['package_weight'] }}" type="text" maxlength="5" class="form-control form-control-inline text-right watch-product-changes">        <div class="input-group-addon">KG</div>      </div>    </div>		<div class="form-group between">      <label for="packagewidth" class="col-md-3 label-customized">  	    <span class="required-field">*</span> Package Width:  	  </label>      <div class="input-group input-box-size">        <input id="package_width" name="package_width" value="{{ $item['package_width'] }}" type="text" maxlength="5" class="form-control form-control-inline text-right watch-product-changes">        <div class="input-group-addon">CM</div>      </div>    </div>		<div class="form-group between">      <label for="packageheight" class="col-md-3 label-customized">  	    <span class="required-field">*</span> Package Height:  	  </label>      <div class="input-group input-box-size">        <input id="package_height" name="package_height" value="{{ $item['package_height'] }}" type="text" maxlength="5" class="form-control form-control-inline text-right watch-product-changes">        <div class="input-group-addon">CM</div>      </div>    </div>		<div class="form-group between">      <label for="packagedepth" class="col-md-3 label-customized">  	    <span class="required-field">*</span> Package Depth:  	  </label>      <div class="input-group input-box-size">        <input id="package_depth" name="package_depth" value="{{ $item['package_depth'] }}" type="text" maxlength="5" class="form-control form-control-inline text-right watch-product-changes">        <div class="input-group-addon">CM</div>      </div>    </div>		